	/**
	 * 取消订单 by yang
	 * @return
	 */
	public void cancelOrder() {
		JSONObject json = new JSONObject();
		response = ServletActionContext.getResponse();
		response.setCharacterEncoding("utf-8");
		response.setContentType("application/json;charset=utf-8");
		Orders mOrder = miaoShaOrderService.findById(orderId);
		Date compareDate = CalendarUtils.mergeHour(new Date(), -6);
		// 订单支付如果超过6小时 则不能操作取消
		if (CalendarUtils.compareDate(mOrder.getPayTime(), compareDate).equals("<")) {
			json.put("overtime", true);
		} else if (mOrder.getOrderStatus() == 0) {
			// 订单取消
			if(miaoShaOrderService.cancelOrder(mOrder)>0)
			{
				json.put("success", true);
			}
			else
			{
				json.put("fail", true);
			}
		} 
		try {
			response.getWriter().print(json.toString());
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
	
	
	
	public String saveOrderMiaoSha() {
		
		JSONObject json = new JSONObject();
		response = ServletActionContext.getResponse();
		response.setCharacterEncoding("utf-8");	
		userVo = getSessionUser();
		user = userVo.getUser();
//		user = userService.findById(1820);
		if(user == null){
			json.put("success", false);
			json.put("", true);
		}else {
			Integer num = orderService.findSellerNumByQi(qi);
			if(miaoShaNum!=null&&num<=miaoShaNum){
				orderNo = "ms" + PublicUtil.getOrderNo();
				order = new OrderJinHuo();
				if(orderAddress!= null){
					if(orderAddress.getId()==null){
						orderAddress.setIsFirst("1");
						orderAddress.setUserId(user.getUserId());
						orderAddressService.save(orderAddress);
					}else {
						OrderAddress oldAddress = orderAddressService.findById(orderAddress.getId());
						oldAddress.setAddress(orderAddress.getAddress());
						oldAddress.setUserName(orderAddress.getUserName());
						oldAddress.setMobile(orderAddress.getMobile());
						oldAddress.setZipcode(orderAddress.getZipcode());
						orderAddressService.update(oldAddress);
					}
				}
				
				System.out.println(">>>>>>>>>>>>>>> total_price "+total_price1);
				
				if(null!=total_price1 && !"".equals(total_price1)){
					StringUtils.replace(total_price1, "￥", "");
					order.setMoney(Double.parseDouble(StringUtils.replace(total_price1, "￥", "")));
					order.setOrdersBH(orderNo);
					orderService.save(order);
					json.put("success", true);
					json.put("ordersBh", orderNo);
				}else{
					json.put("success", false);
					json.put("error", true);
				}
			}else{
				json.put("success", false);
				json.put("num", 0);
			}
			
		}
		try {
			response.getWriter().print(json.toString());
		} catch (IOException e) {
			e.printStackTrace();
		}
		return null;

	}
	
	
