	//计算个人积分，
	public void updateUserTrueJifen(){
		//查询积分状态等于新用户关注的数据
		List jiList = jiFenRecordService.findByHql("from JiFenRecord j where j.status=1 and j.type=5 and j.isEnable is null group by j.fromUserId", null);
		if(jiList.size() > 0){
			Double ordery = null;
			Double miaosha = null;
			boolean isTrue= false;
			for(int x=0;x<jiList.size();x++){
				JiFenRecord jr = (JiFenRecord) jiList.get(x);
				//--订单中formuserId不存在数据是否处理
				if(jr.getFromUserId() == null){
					continue;
				}
				//查询秒杀表中是否存在订单数据,统计该用户是否下过单
				ordery = Double.parseDouble(orderService.findBySql("SELECT if(SUM(money)>0,SUM(money),0) from ordery where userId="+jr.getFromUserId()+" and order_status =1", null).get(0).toString());
				if(ordery > 0d){
					isTrue = true;
				}
				if(isTrue == false){
					//判断秒杀表是否下过单,order_status 获取1 是否正确
					miaosha =Double.parseDouble(orderService.findBySql("SELECT if(SUM(money)>0,SUM(money),0) from miaosha_order where userId="+jr.getFromUserId()+" and order_status =1", null).get(0).toString());
					if(miaosha > 0d){
						isTrue = true;
					}
				}
				if(isTrue == true){
					//更新 积分表数据
					updateJifenStatus(jr.getFromUserId());
				}
				
			}
		}
		
	}
	
	public void updateJifenStatus(Integer userId){
		
	    Connection conn = null;
	    try {
	      Class.forName("com.mysql.jdbc.Driver");

	      conn = DriverManager.getConnection(
	        SystemMessage.getString("DBDRIVER"), SystemMessage.getString("DBLOGINNAME"), SystemMessage.getString("DBPASSWORD"));
	    }
	    catch (Exception e) {
	      System.out.println("数据库连接失败" + e.getMessage());
	    }
    	String userSql = "update jifen_record set isEnable=?,updateDate=? where fromUserId=?";
    	PreparedStatement pst = null;
    	int rs = 0;
    	try {
			pst = conn.prepareStatement(userSql);
			pst.setInt(1, 0);// 数据可用
			pst.setDate(2, (java.sql.Date) new Date());//更新时间
			pst.setInt(3, userId);//数值待确定
			rs = pst.executeUpdate();
			if(rs != 0){ 
				
				} 
		} catch (SQLException e) {
			// TODO Auto-generated catch block
			e.printStackTrace();
		} finally{
		
			if(pst != null){
				try {
					pst.close();
				} catch (SQLException e) {
					// TODO Auto-generated catch block
					e.printStackTrace();
				}
			}
			
			if(conn!=null){
				try {
					conn.close();
				} catch (SQLException e) {
					// TODO Auto-generated catch block
					e.printStackTrace();
				}
			}
		}
	}

	
	
	public void searchOrder(){
		JSONObject jsonObject = new JSONObject();
		String sqls = "select ordersBH,createDate from orders where userId ="+userId+" and order_status = 1 and levelValue like '%"+levelValue+"%'";
		List orders = orderService.findBySql(sqls, null);
		String sqly = "select ordersBH,createDate from ordery where userId ="+userId+" and order_status = 1 and levelValue like '%"+levelValue+"%'";
		List orderys = orderService.findBySql(sqly, null);
		
		SimpleDateFormat format = new SimpleDateFormat("yyyy-MM-dd HH:mm:ss");
		if(orderys.size()>0){
			jsonObject.put("success", true);
			Object[] obj = (Object[]) orderys.get(0);
			jsonObject.put("ordersBH", obj[0]);
			jsonObject.put("createDate", format.format(obj[1]));
		}if(orders.size()>0){
			jsonObject.put("success", true);
			Object[] obj = (Object[]) orders.get(0);
			jsonObject.put("ordersBH", obj[0]);
			jsonObject.put("createDate", format.format(obj[1]));
		}else {
			jsonObject.put("success", false);
		}
		response = ServletActionContext.getResponse();
		response.setCharacterEncoding("utf-8");
	
		try {
			response.getWriter().print(jsonObject.toString());
		} catch (IOException e) {
			e.printStackTrace();
		}
	}
	
